mapper.select:
select
user_id, create_time, update_time, last_login_time, open_id, nickname, phone, email, status
from cdc.user


        and user_id = #{userId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and last_login_time = #{lastLoginTime}


        and open_id = #{openId}


        and nickname = #{nickname}


        and phone = #{phone}


        and email = #{email}


        and status = #{status}
